import { assign, createMachine } from 'xstate';

const formSubmissionMachine = createMachine(
	{
		/** @xstate-layout -IgPJtsQHCQSnE4Sacw3ScpHTWxkDQiJxcOgpVok+zQKM0xms2Yc3mC2szDMsdjlTMzKEYhMUikgA */
		id: 'formSubmissionMachine',
		initial: 'idle',
		context: {
			isSubmitted: false
		},
		states: {
			idle: {
				on: {
					FORM_SUBMIT: 'submitting'
				}
			},
			submitting: {
				on: {
					FORM_SUBMISSION_SUCCESS: 'success',
					FORM_SUBMISSION_FAILURE: 'failure'
				}
			},
			success: {
				entry: 'setSubmitted'
			},
			failure: {
				entry: 'setSubmitted'
			}
		}
	},
	{
		actions: {
			setSubmitted: assign({
				isSubmitted: true
			}),
			setSubmitted: assign({
				isSubmitted: false
			})
		}
	}
);

export default formSubmissionMachine;
